import React, { useState } from "react";

export default function SignupForm({ toggle }) {
  const [form, setForm] = useState({
    name: "",
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: "",
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const validate = () => {
    const usernameRegex = /^[a-zA-Z0-9@._-]+$/;
    const phoneRegex = /^\+\d{1,3}\d{7,}$/;

    if (!form.name || !form.username || !form.email || !form.phone || !form.password || !form.confirmPassword) {
      setError("All fields are required");
      return false;
    }
    if (!usernameRegex.test(form.username)) {
      setError("Username must be alphanumeric and can include @, ., -, _");
      return false;
    }
    if (!phoneRegex.test(form.phone)) {
      setError("Phone number must include country code (e.g. +1234567890)");
      return false;
    }
    if (form.password !== form.confirmPassword) {
      setError("Passwords do not match");
      return false;
    }
    if (form.password === form.username) {
      setError("Password should not be the same as username");
      return false;
    }
    setError("");
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      alert("Signup successful!");
    }
  };

  return (
    <div className="card">
      <div className="header">Create new Account</div>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} />
        <input type="text" name="username" placeholder="Username" value={form.username} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} />
        <input type="text" name="phone" placeholder="Phone No." value={form.phone} onChange={handleChange} />
        <input type="password" name="password" placeholder="New Password" value={form.password} onChange={handleChange} />
        <input type="password" name="confirmPassword" placeholder="Confirm New Password" value={form.confirmPassword} onChange={handleChange} />
        {error && <div className="error">{error}</div>}
        <button type="submit">SIGN UP</button>
      </form>
      <div className="toggle">
        Already have an account? <span onClick={toggle}>Login</span>
      </div>
    </div>
  );
}
